pragma solidity ^0.6.8;

interface IERC20 {
    function name() external view returns (string memory);
    function symbol() external view returns (string memory);
    function decimals() external view returns (uint8);
    function totalSupply() external view returns (uint);
    function balanceOf(address owner) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);
    function approve(address spender, uint value) external returns (bool);
    function transfer(address to, uint value) external returns (bool);
    function transferFrom(address from, address to, uint value) external returns (bool);
    function mint(address to, uint256 amount) external returns (bool);
    function burn(address to, uint256 amount) external;
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

contract OwnableData {
    // V1 - V5: OK
    address public owner;
    // V1 - V5: OK
    address public pendingOwner;
}
// T1 - T4: OK
contract Ownable is OwnableData {
    // E1: OK
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor () public {
        owner = msg.sender;
        emit OwnershipTransferred(address(0), msg.sender);
    }

    // F1 - F9: OK
    // C1 - C21: OK
    function transferOwnership(address newOwner, bool direct, bool renounce) public onlyOwner {
        if (direct) {
            // Checks
            require(newOwner != address(0) || renounce, "Ownable: zero address");

            // Effects
            emit OwnershipTransferred(owner, newOwner);
            owner = newOwner;
            pendingOwner = address(0);
        } else {
            // Effects
            pendingOwner = newOwner;
        }
    }

    // F1 - F9: OK
    // C1 - C21: OK
    function claimOwnership() public {
        address _pendingOwner = pendingOwner;
        
        // Checks
        require(msg.sender == _pendingOwner, "Ownable: caller != pending owner");

        // Effects
        emit OwnershipTransferred(owner, _pendingOwner);
        owner = _pendingOwner;
        pendingOwner = address(0);
    }

    // M1 - M5: OK
    // C1 - C21: OK
    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }
}

contract TokenVesting is Ownable {
    
  using SafeMath for uint256;
  using SafeERC20 for IERC20;

  uint256 public cliff;
  uint256 public start;
  uint256 public duration;

  struct Locker {
      uint amount;
      uint released;
  }
  // 0.token  1.user
  mapping (address=>mapping (address=>Locker)) public locked;
  
  event Unlock(address indexed,uint256);
  event Lock(address indexed,uint256);
  
  constructor(uint256 _start) public {
      start = _start;
  }
  
  function setDuration(uint256 _cliff,uint256 _duration) public onlyOwner {
      cliff = start.add(_cliff);
      duration = _duration;
  }
  
  function lock(IERC20 token,uint256 amount) public {
      Locker storage locker = locked[address(token)][msg.sender];
      require(locker.amount==0,"have locked");
      require(block.number < cliff,"Has begun to release");
      locker.amount = amount;
      token.safeTransferFrom(msg.sender,address(this),amount);
      emit Lock(msg.sender,amount);
  }

  function unlock(IERC20 token) public {
    Locker storage locker = locked[address(token)][msg.sender];
    uint256 amount = locker.amount;
    
    require(amount>locker.released,"after release");

    uint256 _releasableAmount;
    if (block.number > cliff) {
        _releasableAmount = amount.mul(block.number.sub(start).div(duration));
    }
      
    if(_releasableAmount>amount) {
        _releasableAmount = amount;
    }
      
    uint256 showRelease = _releasableAmount.sub(locker.released,"not enough for release");
      
    locker.released = _releasableAmount;
    
    safeTokenTransfer(token, msg.sender, showRelease);
    
    emit Unlock(msg.sender,showRelease);
  }
  
  function safeTokenTransfer(IERC20 token,address _to, uint256 _amount) internal {
        uint256 bal = token.balanceOf(address(this));
        if (_amount > bal) {
            token.safeTransfer(_to, bal);
        } else {
            token.safeTransfer(_to, _amount);
        }
    }
}

